package com.loop;

public class WhileProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;
		int i =0;
		while(num <=100) {
			//num = num + 10;
			num = num + 50;

			System.out.println("Iterating : " + i);
			i++;
		}
		
	}

}
